mapper.select:
SELECT id_title idTitle, title
FROM travel
ORDER BY start_date DESC;

SELECT id_title idTitle, title
FROM travel
WHERE id_region = #{idRegion}
ORDER BY start_date DESC;

SELECT id_title idTitle, title
FROM travel
WHERE id_region = #{idRegion}
AND id_region_detail = #{idRegionDetail}
ORDER BY start_date DESC;
