mapper.select:
SELECT region
from region
where id_region = #{idRegion}

SELECT region_detail
from region_detail
where id_region_detail = #{idRegionDetail}

mapper.update:
UPDATE travel
SET del_yn = 1 - del_yn
WHERE id_title = #{idTitle}

UPDATE travel
SET del_yn = 1 - del_yn
WHERE id_title = #{idTitle}
