mapper.select:
SELECT id_sub idSub, sub_title subTitle, finish_yn finishYn
FROM sub_title
WHERE id_title = #{idTitle}

SELECT id_check idCheck, check_list checkList, finish_yn finishYn
FROM check_list
WHERE id_sub = #{idSub}

SELECT id_sub
FROM sub_title
WHERE id_title = #{idTitle}

SELECT region
from region
where id_region = #{idRegion}

SELECT region_detail
from region_detail
where id_region_detail = #{idRegionDetail}

mapper.update:
UPDATE travel
SET del_yn = 1 - del_yn
WHERE id_title = #{idTitle}
